                weights[i] = reader.ReadDouble();
            }
            setWeights(weights);
            fitness = reader.ReadDouble();
        }
        public void Save(BinaryWriter writer)
        {
            writer.Write(numLayers);
            for (int x = 0; x < numNeurons.Count(); x++)
